package com.zhoubi.graindepot.biz;import com.zhoubi.graindepot.bean.BaseElement;import com.zhoubi.graindepot.mapper.BaseElementMapper;import com.zhoubi.graindepot.base.BaseMapper;import com.zhoubi.graindepot.base.BaseService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.ArrayList;import java.util.List;import java.util.Map;@Servicepublic class BaseElementBiz extends BaseService<BaseElement>  {	@Autowired	private BaseElementMapper BaseElementMapper;	@Override	protected BaseMapper<BaseElement> getMapper() {		return BaseElementMapper;	}	public List<Integer> getAuthorityElement(Map m) {		List<BaseElement> list=BaseElementMapper.getAuthorityElement(m);		List<Integer> result=new ArrayList();		for(BaseElement e:list){			result.add(e.getElementid());		}		return result;	}}